	removeVisible(findFormField('combobox_4'));
} 
if (item == "через организацию почтовой связи") {
	removeVisible(findFormField('textfield_29')); // наименование кр. орг. или БИК кр. орг.
	removeVisible(findFormField('textfield_30')); // Корреспондентский счёт
	removeVisible(findFormField('textfield_31')); // Счет заявителя

	addVisible(findFormField('combobox_4')); // Получение
}
