function fuk(){
	document.getElementById('mfcCombo-triggerWrap').onmouseover = function() {
	//confirm("Получение результата по данной услуге исключительно в ОВ!!!");
	var div = document.createElement('div');
	div.setAttribute("id", "output_documents");
	document.getElementById('mfcCombo-triggerWrap').appendChild(div);
	document.getElementById('output_documents').style.cssText="width: 100%;\
	height: 89px;\
	background-color: #f0724a;\
	position: absolute;\
	overflow: hidden;\
	clear: both;\
	z-index: 1000;\
	top: 45px;\
	right: 0px;\
	display: block;\
	border: 2px dashed #ff3f05;\
	margin: 0 auto;\
	text-align:center;\
	padding-top: 22px;\
	";
	document.getElementById('output_documents').innerHTML = "<p>Место выдачи результата услуги -<br />исключительно ОГВ!</p>"
	};


	document.getElementById('mfcCombo-triggerWrap').onmouseout = function() {
	var div = document.createElement('div');
	div.setAttribute("id", "output_documents");
	document.getElementById('mfcCombo-triggerWrap').appendChild(div);
	document.getElementById('output_documents').style.cssText="display: none;";
	};
};

