var birthPlaceType = Ext.ComponentQuery.query('[name=birthPlaceType]')[0];

bpt(birthPlaceType)


if (birthPlaceType) {
    birthPlaceType.addListener('change', bpt(birthPlaceType))
}

function bpt(birthPlaceType) {
    var fieldset_5 = Ext.ComponentQuery.query('[name=fieldset_5]')[0];
    var fieldset_6 = Ext.ComponentQuery.query('[name=fieldset_6]')[0];
    var fieldset_7 = Ext.ComponentQuery.query('[name=fieldset_7]')[0];
    var fieldset_8 = Ext.ComponentQuery.query('[name=fieldset_8]')[0];
    if (birthPlaceType.value == 'Особое') {
            addVisible(fieldset_5);
            removeVisible(fieldset_6);
            removeVisible(fieldset_7);
            removeVisible(fieldset_8);
        } else if (birthPlaceType.value == 'Стандартное') {
            removeVisible(fieldset_5);
            addVisible(fieldset_6);
            addVisible(fieldset_7);
            addVisible(fieldset_8);
        } else {
            removeVisible(fieldset_5);
            removeVisible(fieldset_6);
            removeVisible(fieldset_7);
            removeVisible(fieldset_8);
        }
}

var district = Ext.ComponentQuery.query('[name=district]')[0];
