var registrationAddress = Ext.ComponentQuery.query('[name=registrationAddress]')[0];
var registrationAddressKladr= Ext.ComponentQuery.query('[name=registrationAddressKladr]')[0];

if (regAddressType.disabled==false) {
if (regAddressType.value=='Российский')  {
removeVisible(regCountryCode);
removeVisible(foreingRegAddress);
addVisible(registrationAddress);
addVisible(registrationAddressKladr);
} else if (regAddressType.value=='Иностранный') {
addVisible(regCountryCode);
addVisible(foreingRegAddress);
removeVisible(registrationAddress);
removeVisible(registrationAddressKladr);
} else {
removeVisible(regCountryCode);
removeVisible(foreingRegAddress);
removeVisible(registrationAddress);
removeVisible(registrationAddressKladr);
}}

