var representative_check = Ext.ComponentQuery.query('[name=representative_check]')[0];
var checkbox_5 = Ext.ComponentQuery.query('[name=checkbox_5]')[0];
var fileuploadpanel_1 = Ext.ComponentQuery.query('[fieldName=fileuploadpanel_1]')[0];


if (checkbox_5.checked == true)
{
representative_check.setVisible(false);
representative_check.setValue(true);
fileuploadpanel_1.hide();
}
else
{
representative_check.setVisible(false);
representative_check.setValue(false);
fileuploadpanel_1.show();
}

var textfield_31 = Ext.ComponentQuery.query('[name=textfield_31]')[0];
var textfield_8 = Ext.ComponentQuery.query('[name=textfield_8]')[0];

str = textfield_31.getValue();
switch (str) {
  case "Паспорт гражданина Российской Федерации":
    textfield_8.setValue("21");
    break;
  case "Свидетельство о рождении гражданина РФ":
    textfield_8.setValue("03");
    break;
  case "Временное удостоверение личности гражданина Российской Федерации":
    textfield_8.setValue("14");
    break;
  case "Военный билет":
    textfield_8.setValue("07");
    break;
  case "Удостоверение личности военнослужащего Российской Федерации":
    textfield_8.setValue("24");
    break;
  case "Паспорт иностранного гражданина":
    textfield_8.setValue("10");
    break;
  case "Свидетельство о предоставлении временного убежища на территории Российской Федерации":
    textfield_8.setValue("18");
    break;
  case "Вид на жительство в Российской Федерации":
    textfield_8.setValue("12");
    break;
  case "Удостоверение беженца":
    textfield_8.setValue("13");
    break;
  case "Разрешение на временное проживание":
    textfield_8.setValue("15");
    break;
  case "Свидетельство о рождении иностранного гражданина":
    textfield_8.setValue("23");
    break;
  default:
    textfield_8.setValue("21");
}
