console.log('loaded');
var checkExist = setInterval(function() {
    if (document.getElementById('button-1042')) {
        console.log('button founded');
        document.getElementById('button-1042').addEventListener('click', function() {
            if (Ext.Object.fromQueryString(location.search).eid !== null) {
                let is_mine = 0;

                let template = Ext.getCmp("printTemplateButton").printTemplateText;

                service = "<ahk-json>" + JSON.stringify(JSON.parse("[" + Ext.getCmp("getFormJSON").getJSON().toString() + "]"), null, 4) + "</ahk-json>";
                service = service + "<ahk-title>" + Ext.getCmp('serviceTitle').value + "</ahk-title>";
                service = service + "<ahk-id>" + Ext.Object.fromQueryString(location.search).eid + "</ahk-id>";

                if (template === "") {
                    let conTemplate = Ext.getCmp("conditionalPrintTemplateButton").conditionalPrintTemplates;
                    if (conTemplate.length > 0) {
                            let templates = "";
                        for (let i = 0; i < conTemplate.length; i++) {
                            templates = templates + "<ahk-template_" + i + ">";
                            let name = "<ahk-name>" + conTemplate[i].fieldName + "</ahk-name>";
                            let value = "<ahk-value>" + conTemplate[i].fieldValue + "</ahk-value>";
                            templates = templates + name + value + "<ahk-template>" + conTemplate[i].htmlContent + "</ahk-template>";
                            templates = templates + "</ahk-template_" + i + ">";
                            if (conTemplate[i].htmlContent.indexOf('applethecandy') != -1) { is_mine = 1 }
                        }

                        service = service + "<ahk-count>" + conTemplate.length + "</ahk-count>";
                        service = service + templates;
                        service = service + "<ahk-is_mine>" + is_mine + "</ahk-is_mine>";

                        download('service.txt', service);
                    }
                } else {
                    if (template.indexOf('applethecandy') != -1) { is_mine = 1 }

                    service = service + "<ahk-count>1</ahk-count>";
                    service = service + "<ahk-template>" + template + "</ahk-template>";
                    service = service + "<ahk-is_mine>" + is_mine + "</ahk-is_mine>";

                    download('service.txt', service);
                }
            }

        }, false);
        clearInterval(checkExist);
    }
}, 100);

function download(filename, text) {
    var element = document.createElement('a');
    element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
    element.setAttribute('download', filename);

    element.style.display = 'none';
    document.body.appendChild(element);

    element.click();

    document.body.removeChild(element);
}
